mapper.insert:
insert into employees
(
    id
    ,name
    ,lastname
    ,birthdate
    ,role
    ,department
    ,email
)
values
(
    null
    ,#{name}
    ,#{lastName}
    ,#{birthDate}
    ,#{role}
    ,#{department}
    ,#{email}
)

mapper.update:
update employees
set
    name = #{name}
    ,lastname = #{lastName}
    ,birthdate = #{birthDate}
    ,role = #{role}
    ,department = #{department}
    ,email = #{email}
where
    id = #{id}
